DECLARE @tblAccounts	Table (
		Company			Varchar(5),
		Intercompany	Varchar(5),
		Account			Varchar(15), 
		Description		Varchar(50))

-- ="INSERT INTO IntercompanyReport_Accounts (Company, Intercompany, Account, Description) VALUES ('"&A2&"','"&B2&"','"&C2&"','"&D2&"')"

UPDATE IntercompanyReport_Accounts SET Inactive = 1



SELECT	DATA.*
FROM	@tblAccounts DATA
		LEFT JOIN IntercompanyReport_Accounts ACCT ON DATA.Company = ACCT.Company AND DATA.Intercompany = ACCT.Intercompany AND DATA.Account = ACCT.Account AND ACCT.Inactive = 0
WHERE	ACCT.Account IS NULL
		--AND DATA.Company in ('PTS','NDS','IMCCS')

SELECT	*
FROM	IntercompanyReport_Accounts
WHERE	Inactive = 0
ORDER BY Company, Intercompany, Account

