SET NOCOUNT ON

DECLARE	@Integration	varchar(6) = 'BALAR',
		@BatchId		Varchar(25),
		@Company		Varchar(5) = 'EMP0',
		@RunDate		Date = GETDATE(),
		@DatePortion	Varchar(15)

SET @DatePortion	= dbo.PADL(MONTH(@RunDate), 2, '0') + dbo.PADL(DAY(@RunDate), 2, '0') + RIGHT(dbo.PADL(YEAR(@RunDate), 4, '0'), 2) + dbo.PADL(DATEPART(HOUR, GETDATE()), 2, '0') + dbo.PADL(DATEPART(MINUTE, GETDATE()), 2, '0')
SET @BatchId		= @Integration + @DatePortion

DECLARE	@tblBalances Table (
		CustomerId		Varchar(12),
		InvoiceNum		Varchar(25),
		RMDTYPAL		Int,
		DISTTYPE		Int,
		InvDate			Date,
		PostingDate		Date,
		Amount			Numeric(10,2),
		GLAccount		Varchar(20),
		Description		Varchar(30))

DECLARE @curdata TABLE (Customer Varchar(12), Document Varchar(30), DocDate Date, Amount Numeric(10,2), Account Varchar(15), RowType Varchar(10))

/*
="INSERT INTO @curdata (Customer, Document, DocDate, Amount, Account, RowType) VALUES ('"&A3&"','"&B3&"','"&TEXT(D3,"mm/dd/yyyy")&"',"&F3&",'"&G3&"','"&H3&"')"
*/


--SELECT	Customer, Document, DocDate, ABS(Amount) AS Amount, Account, 
--		IIF((rowtype = 'RECV' and amount > 0) or (rowtype = 'SALES' and amount < 0), 1, 7) AS RMDTYPAL,
--		CASE rowtype WHEN 'RECV' THEN 3
--					 WHEN 'SALES' THEN 9
--					 ELSE 19 END AS DISTTYPE
--FROM	@curdata
--ORDER BY CUSTOMER,DOCUMENT,7

SELECT	[Customer]
		,[Document]
		,IIF((rowtype = 'RECV' and amount > 0) or (rowtype = 'SALES' and amount < 0), 1, 7) AS RMDTYPAL
		,CASE rowtype WHEN 'RECV' THEN 3
					 WHEN 'SALES' THEN 9
					 ELSE 19 END AS DISTTYPE
		,[DocDate]
		,'03/13/2021' AS PostingDate
		,Amount
		,Account
		,'Document ' +[Document] AS Description
FROM	@curdata

DECLARE	@DOCNUMBR		varchar(20)
		,@DOCDESCR		varchar(30)
		,@CUSTNMBR		varchar(15)
		,@DOCDATE		date
		,@DUEDATE		date
		,@DOCAMNT		money
		,@SLSAMNT		money
		,@RMDTYPAL		int
		,@DISTTYPE		int
		,@ACTNUMST		varchar(15)
		,@DEBITAMT		money
		,@CRDTAMNT		money
		,@DistRef		varchar(30)
		,@UserId		varchar(25) = 'AR Integrator'
		,@PostingDate	date

DELETE	ReceivedIntegrations
WHERE	Company = @Company
		AND BatchId = @BatchId

DELETE	Integrations_AR
WHERE	Company = @Company
		AND BatchId = @BatchId

DECLARE curData CURSOR LOCAL KEYSET OPTIMISTIC FOR
SELECT	InvoiceNum,
		IIF(RMDTYPAL = 1,'INV ','CRD ') + InvoiceNum,
		CustomerId,
		InvDate,
		DATEADD(dd, 30, InvDate),
		ABS(Amount),
		ABS(Amount),
		RMDTYPAL,
		DISTTYPE,
		GLAccount,
		IIF(DISTTYPE NOT IN (9, 19), ABS(Amount), 0),
		IIF(DISTTYPE IN (9, 19), ABS(Amount), 0),
		Description,
		PostingDate
FROM	@tblBalances
ORDER BY CustomerId, InvoiceNum, DISTTYPE

OPEN curData 
FETCH FROM curData INTO @DOCNUMBR, @DOCDESCR, @CUSTNMBR, @DOCDATE, @DUEDATE, @DOCAMNT, @SLSAMNT, @RMDTYPAL,
						@DISTTYPE, @ACTNUMST, @DEBITAMT, @CRDTAMNT, @DistRef, @PostingDate

WHILE @@FETCH_STATUS = 0 
BEGIN
	EXECUTE USP_Integrations_AR @Integration, @Company, @BatchId, @DOCNUMBR, @DOCDESCR, @CUSTNMBR, @DOCDATE, @DUEDATE,
								@DOCAMNT, @SLSAMNT, @RMDTYPAL, @DISTTYPE, @ACTNUMST, @DEBITAMT, @CRDTAMNT, @DistRef, 
								Null, Null, Null, Null, 0, 0, 0, @UserId, @PostingDate

	FETCH FROM curData INTO @DOCNUMBR, @DOCDESCR, @CUSTNMBR, @DOCDATE, @DUEDATE, @DOCAMNT, @SLSAMNT, @RMDTYPAL,
							@DISTTYPE, @ACTNUMST, @DEBITAMT, @CRDTAMNT, @DistRef, @PostingDate
END

CLOSE curData
DEALLOCATE curData

SELECT	*
FROM	Integrations_AR
WHERE	Company = @Company
		AND BatchId = @BatchId
ORDER BY CUSTNMBR, DOCNUMBR

PRINT @BatchId

IF @@ERROR = 0
